import React from 'react'
import { Link } from "react-router-dom";
import MainHeader from '../../components/main-header/main-header.component';


class SignIn extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            username:'',
            password:''
        }
    }



    sumitForm = () => {
        let username = this.state.username;
        let password = this.state.password;
        if(!username || !username.length){
            ToastsStore.warning("username required", 3000, "toast-box-pink")
            return
        }
        if(!password || !password.length){
            ToastsStore.warning("password required", 3000, "toast-box-pink")
            return
        }
        let url = FormatUrl(`/register`)
        fetch(url, {
            method: 'post',
            headers:{
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                username: username,
                password:password
            })
            })
        .then(response => response.json())
        .then(res => {
            if(res.code){
            ToastsStore.success("Registration successful", 3000, "toast-box-pink")
            this.props.history.push("/login")
                } else {
            ToastsStore.error("Registration failed", 3000, "toast-box-pink")
            }
        }).catch(err => {
            ToastsStore.error("Internal server erro", 3000, "toast-box-pink")
        })
    }

    cancelForm = () => {
        document.getElementById("username").value = ""
        document.getElementById("password").value = ""
    }


    render(){
        return (
            <div id="site">
            <MainHeader />
            <div className="main-w3layouts wrapper" style={{ backgroundImage:`url("https://themeim.com/demo/milando/media/banner/6.jpg")` }}>
            <br/><br/><br/><br/>
                <h1>SignIn Form</h1>
                <div className="main-agileinfo">
                    <div className="agileits-top">
                        <input className="text email" type="text" placeholder="Username" required />
                        <input className="text" type="password" placeholder="Password" required />
                        <div className="row">
                            <div className="col-sm-6">
                                <button type="button" className="form-button">Ok</button>
                            </div>
                            <div className="col-sm-6">
                                <button type="button" className="form-button">Cancel</button>
                            </div>
                        </div>
                        <p>Don't have an Account? <Link to="/register"> Register Now!</Link></p>
                    </div>
                </div>
                <br/><br/>
            </div>
            </div>
        )
    }
}


export default SignIn;
